mapper.sql:

(agent.Dept_name NOT LIKE '%飞鹤航空%' and 
agent.Dept_name  NOT LIKE '%上航会展%' and
agent.Dept_name  NOT LIKE '%上航旅汽%')


and class = #{status}


and type = #{type}


and belong = #{belong}


and equip_no LIKE CONCAT(CONCAT('%',#{equip_no}),'%')


and username LIKE CONCAT(CONCAT('%',#{username}),'%')



and sn LIKE CONCAT(CONCAT('%',#{sn}),'%')

mapper.select:
;with t as ( select ROW_NUMBER() over(order by ITequipment.id DESC) as pnum,
FROM ITequipment LEFT JOIN agent ON ITequipment.dept_id = agent.ID LEFT JOIN cwit ON cwit.cwid=ITequipment.id
and agent.order_id LIKE CONCAT((select order_id from agent where agent.id=#{dept_id}),'%')
) SELECT * FROM t where pnum BETWEEN (#{pgstr}-1)*#{pgsiz}+1 and #{pgstr}*#{pgsiz}

;with t as ( select ROW_NUMBER() over(order by ITequipment.id DESC) as pnum,
FROM ITequipment LEFT JOIN agent ON ITequipment.dept_id = agent.ID LEFT JOIN cwit ON cwit.cwid=ITequipment.id
and agent.order_id in ('00','01','02','03','04','10','12','13','14','15')
) SELECT * FROM t where pnum BETWEEN (#{pgstr}-1)*#{pgsiz}+1 and #{pgstr}*#{pgsiz}
